import {Component} from "react";

class SquareNumber extends Component {
    render() {
        return (
            <div className={`inline m-auto ${this.props.color} w-[94%] h-[94%] rounded-lg`}>
                <p className={`text-center font-bold align-middle`}>{this.props.number}</p>
            </div>
        );
    }
}

export default SquareNumber
