import {Component} from "react";
import SquareNumber from './SquareNumber'

const DEFAULT_COLOR = 'bg-lime-500'
const DEFAULT_VALUE = '1024'
const ALL_VALUES = [2, 4, 8, 16, 32, 64, 128, 256, 512, 1024, 2048]
const BOARD_DEFINITION = [
    {'id': '00', 'color': 'bg-red-300', 'value': 2},
    {'id': '01', 'color': 'bg-orange-500', 'value': 4},
    {'id': '02', 'color': 'bg-lime-500', 'value': 8},
    {'id': '03', 'color': 'bg-green-500', 'value': 16},
    {'id': '10', 'color': 'bg-emerald-500', 'value': 32},
    {'id': '11', 'color': 'bg-cyan-500', 'value': 64},
    {'id': '12', 'color': 'bg-sky-500', 'value': 128},
    {'id': '13', 'color': 'bg-indigo-500', 'value': 256},
    {'id': '20', 'color': 'bg-purple-500', 'value': 512},
    {'id': '21', 'color': 'bg-fuchsia-500', 'value': 1024},
    {'id': '22', 'color': 'bg-rose-500', 'value': 2048},
    {'id': '23', 'color': DEFAULT_COLOR, 'value': DEFAULT_VALUE},
    {'id': '30', 'color': DEFAULT_COLOR, 'value': DEFAULT_VALUE},
    {'id': '31', 'color': DEFAULT_COLOR, 'value': DEFAULT_VALUE},
    {'id': '32', 'color': DEFAULT_COLOR, 'value': DEFAULT_VALUE},
    {'id': '33', 'color': DEFAULT_COLOR, 'value': DEFAULT_VALUE},
]


class Board extends Component {
    render() {
        return (
            <div className="flex h-screen">
                <div className="flex grid m-auto bg-gray-400 border-2 rounded-md w-96 h-96 grid-rows-4 grid-cols-4">
                    {BOARD_DEFINITION.map((square_definition) => (
                        <SquareNumber
                            color={square_definition.color}
                            value={square_definition.id}
                        />
                    ))}
                </div>
            </div>
        );
    }
}

export default Board
