import {Component} from "react";

class SquareNumber extends Component {
    render() {
        return (
            <div className={`m-auto bg-${this.props.color} w-[94%] h-[94%] rounded-lg align-middle text-center font-bold`}>
                <p className={`text-[100%]`}>{
                    this.props.number}
                </p>
            </div>
        );
    }
}

export default SquareNumber
