import {Component} from "react";
import SquareNumber from './SquareNumber'

const DEFAULT_COLOR = 'black'
const DEFAULT_VALUE = '1024'
const BOARD_DEFINITION = [
    {'id': '00', 'color': DEFAULT_COLOR, 'value': DEFAULT_VALUE},
    {'id': '01', 'color': DEFAULT_COLOR, 'value': DEFAULT_VALUE},
    {'id': '02', 'color': DEFAULT_COLOR, 'value': DEFAULT_VALUE},
    {'id': '03', 'color': DEFAULT_COLOR, 'value': DEFAULT_VALUE},
    {'id': '10', 'color': DEFAULT_COLOR, 'value': DEFAULT_VALUE},
    {'id': '11', 'color': DEFAULT_COLOR, 'value': DEFAULT_VALUE},
    {'id': '12', 'color': DEFAULT_COLOR, 'value': DEFAULT_VALUE},
    {'id': '13', 'color': DEFAULT_COLOR, 'value': DEFAULT_VALUE},
    {'id': '20', 'color': DEFAULT_COLOR, 'value': DEFAULT_VALUE},
    {'id': '21', 'color': DEFAULT_COLOR, 'value': DEFAULT_VALUE},
    {'id': '22', 'color': DEFAULT_COLOR, 'value': DEFAULT_VALUE},
    {'id': '23', 'color': DEFAULT_COLOR, 'value': DEFAULT_VALUE},
    {'id': '30', 'color': DEFAULT_COLOR, 'value': DEFAULT_VALUE},
    {'id': '31', 'color': DEFAULT_COLOR, 'value': DEFAULT_VALUE},
    {'id': '32', 'color': DEFAULT_COLOR, 'value': DEFAULT_VALUE},
    {'id': '33', 'color': DEFAULT_COLOR, 'value': DEFAULT_VALUE},
]


class Board extends Component {
    render() {
        return (
            <div className="flex h-screen">
                <div className="flex grid m-auto bg-gray-400 w-96 h-96 rounded-lg grid-rows-4 grid-cols-4">
                    {BOARD_DEFINITION.map((square_definition) => (
                        <SquareNumber
                            color={square_definition.color}
                            value={square_definition.id}
                        />
                    ))}
                </div>
            </div>
        );
    }
}

export default Board
